import pandas

def adjust_location(array, x_value_to_adjust, y_value_to_adjust): 
    for array_value in array:
        if type(array_value) is list:
            adjust_location(array_value, x_value_to_adjust, y_value_to_adjust)
        else:
            array[0] = array[0] + x_value_to_adjust
            array[1] = array[1] + y_value_to_adjust
            break   

def adjust_size(array, ratio): 
    for array_value in array:
        if type(array_value) is list:
            adjust_size(array_value, ratio)
        else:
            array[0] = array[0] * ratio
            array[1] = array[1] * ratio
            break
  
